##### Part 1 #####
def FindFloor(input):
    up = down = 0
    for direction in input:
        if direction == "(":
            up += 1
        else:
            down += 1
    return up, down

##### Part 2 #####
def inBasement(input):
    current_floor = 0
    for i in range(len(input)):
        if input[i] == "(":
            current_floor += 1
        else:
            current_floor -= 1
        if current_floor < 0:
            return i+1 # Because Arrays are supposed to start with 0!!

if __name__ == '__main__':
    up, down = FindFloor(input)
    print("Day 1 Part 1: %i" % (up-down))
    print("Day 1 Part 2: %i" % inBasement(input))
